def chunk_indices(sentence_lengths):
    count = 0
    start_index = 0
    index_bin = []
    for i, v in enumerate(sentence_lengths):
        if count < 800:
            count += v
        else:
            index_bin.append((start_index, i-1))
            print(count)
            start_index = i
            count = v
    print(index_bin)

